import React, { useState, useEffect } from "react";
import axios from "axios";
import { 
  Download, 
  Search, 
  Grid, 
  List, 
  Image, 
  Video, 
  Globe, 
  Settings, 
  Moon, 
  Sun 
} from 'lucide-react';
import VideoPlayerComponent from "./VideoPlayerComponent";

const Snapdown = () => {
  const [username, setUsername] = useState("");
  const [stories, setStories] = useState([]);
  const [viewMode, setViewMode] = useState("list");
  const [filter, setFilter] = useState("all");
  const [error, setError] = useState("");
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);

  // Fetch stories logic remains the same...

  return (
    <div className="snapdown-container">
      <h1>
        <img src="/snapdown-logo.png" alt="Snapdown" width="50" /> 
        Snapdown
      </h1>

      <div id="poda">
        <input
          className="input"
          type="text"
          placeholder="Enter Snapchat username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <button className="button" onClick={fetchStories}>
          <Search size={20} /> Search
        </button>
      </div>

      <div className="view-filter-container">
        <button 
          className={`button ${viewMode === 'list' ? 'active' : ''}`} 
          onClick={() => setViewMode("list")}
        >
          <List size={20} /> List View
        </button>
        <button 
          className={`button ${viewMode === 'grid' ? 'active' : ''}`} 
          onClick={() => setViewMode("grid")}
        >
          <Grid size={20} /> Grid View
        </button>
        
        <button 
          className={`button ${filter === 'all' ? 'active' : ''}`} 
          onClick={() => setFilter("all")}
        >
          <Globe size={20} /> All
        </button>
        <button 
          className={`button ${filter === 'images' ? 'active' : ''}`} 
          onClick={() => setFilter("images")}
        >
          <Image size={20} /> Images
        </button>
        <button 
          className={`button ${filter === 'videos' ? 'active' : ''}`} 
          onClick={() => setFilter("videos")}
        >
          <Video size={20} /> Videos
        </button>
      </div>

      <div className={viewMode === "list" ? "list-view" : "grid-view"}>
        {stories
          .filter((story) => {
            if (filter === "images") return story.isImage;
            if (filter === "videos") return story.isVideo;
            return true;
          })
          .map((story, index) => (
            <div key={index} className="story-item">
              {story.isVideo ? (
                <VideoPlayerComponent url={story.url} />
              ) : (
                <img src={story.url} alt={`Story ${index}`} />
              )}
              <button 
                className="download-btn" 
                onClick={() => downloadStory(story.url)}
              >
                <Download size={20} /> Download
              </button>
            </div>
          ))}
      </div>

      {/* Settings modal and other components remain the same */}
    </div>
  );
};

export default Snapdown;
